allUsers.map:
function(doc) {
  if(doc.type === 'user' && doc.source == 'bitbucket') {
    emit(doc.sourceData.display_name, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'blizzard-eu') {
    emit(doc.sourceData.battletag, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'facebook') {
    emit(doc.sourceData.name, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'github') {
    emit(doc.sourceData.login, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'google') {
    emit(doc.sourceData.name, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'linkedin') {
    emit(doc.sourceData.firstName + ' ' + doc.sourceData.lastName, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'twitter') {
    emit(doc.sourceData.screenName, doc.source);
  }
  if(doc.type === 'user' && doc.source == 'windowslive') {
    emit(doc.sourceData.displayName, doc.source);
  }

}
