allTags.map:
function(doc) {
  if(doc.type === 'link') {
    for(var i = 0 ; i < doc.tags.length ; i++) {
      emit([doc.userid, doc.tags[i]], 1);
    }
  }
}

byUseridAndTag.map:
function(doc) {
  if(doc.type === 'link') {
    for(var i = 0; i < doc.tags.length; i++) {
      emit([doc.userid, doc.tags[i]], doc);
    }
  }
}
